import React from 'react'
import PaginatorBtn from './PaginatorBtn'
const Paginator = (props)=> {

    const {next, prev, count} = props.info
    const current = props.current
    let pageButtons;

    let totalPages = Math.ceil(count/20)
    console.log(totalPages)



    return (
      <div className="my-3 text-center">
        <div>{count} Results</div>
        {prev && <PaginatorBtn setPage={props.setPage} num={current-1} text="<"/>}
        Current Page: {current}
        {next && (
          <button
            className="ml-3 direction-btn"
            onClick={() => {
              props.setPage(current + 1);
            }}
          >
            {">>"}
          </button>
        )}
      </div>
    );
}

export default Paginator
