import React, { useState, useEffect } from "react";
import axios from "axios";
import Nav from './components/Nav'
import moment from 'moment'
import Options from './components/Options'
import MainData from './components/MainData'
import SupportedHeader from './components/SupportedHeader'
const App = () => {
    const [jobs, setJobs] = useState([]);
    const [loading, setLoading] = useState(true)
    const [failure, setFailure] = useState(false)
    const [lastUpdate, setLastUpdate] = useState({})
    const [stack, setStack] = useState([])

    const [page, setPage] = useState(1)
    const [sort, setSort] = useState("default")
    const [company, setCompany] = useState(null)
    const [queries, setQueries] = useState(['Java', 'Android'])

    useEffect(() => {
      let stackUrlString;
      if (queries && queries.length > 0) {
        stackUrlString = `&stack=${queries.join("&stack=")}`
      }
      let companyUrlString;
      if (company) {
        companyUrlString = `&company=${company}`
      }
        axios.get(`/hello?&sort=${sort}${stackUrlString}${companyUrlString}`)
        .then((res) => {
            console.log(res.data)
            setJobs(res.data.response.jobs)
            setLastUpdate((prev) => {
              return {
                ...prev,
                jobs: res.data.response.jobs[0].created_at,
              };
            });
            setLoading(false)
        })
        .catch((err) => {
          setLoading(false)
          setFailure(true)
          console.log(`Error in axios request`, err)
        })

        axios.get("/tech")
        .then((res) => {
            setStack(res.data)
            setLastUpdate(prev => {
              return {...prev, tech: res.data[res.data.length-1].created_at}
            })
        })
        .catch((err) => {
          console.log(`Error in axios request for tech`, err)
        })
    }, []);

    useEffect(()=> {
        console.log(jobs)
        console.log(stack)
        console.log(lastUpdate)
    }, [jobs, stack, lastUpdate])

    return (
      <div>
        <Nav />
        <div className="container">


          <SupportedHeader
            lastUpdate={moment(lastUpdate.tech).format("DD MMMM YYYY")}
            stack={stack}
          />

          <MainData
            loading={loading}
            failure={failure}
            lastUpdate={moment(lastUpdate.jobs).format("DD MMM, h:mm:ss a")}
            data={jobs}
          />

        </div>
      </div>
    );

};


export default App;
