import React from 'react'

const StackBtn = (props)=> {

    return (
      <div className={`stack-btn stack-${props.name.toLowerCase()} mr-2 mb-1`}>
        {props.name} <i class={`devicon-${props.name.toLowerCase()}-plain`}></i>
      </div>
    );
}

export default StackBtn
